import React, { useState } from 'react';
import axios from 'axios';
import backgroundImage from './Bill.jpg'; 
import './App.css';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [sales, setSales] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      setClients(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClientClick = async (customerId) => {
    try {
      const response = await axios.get(`http://localhost:8000/sales/${customerId}`);
      setSales(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (

    <div style={{ padding: "20px" }}>
      <img src={backgroundImage} alt="background" style={{width: '100%', height: '40vh', objectFit: 'cover'}} />

      <h1 spellcheck="true" placeholder="Sans titre" data-content-editable-leaf="true" contenteditable="false" >Apprenticeship - Test technique Full-Stack</h1>

      <h2>Veuillez entrer un nom :</h2>
      <input style={{ padding: "10px", marginBottom: "20px" }} placeholder='' value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button style={{ padding: "10px", marginLeft: "10px" }} onClick={handleSearch}>Rechercher</button>
      <ul>
        <h1>Information du client {inputValue} :</h1>
        {clients.map(client => (
          <li key={client.customers_id} style={{ marginBottom: "20px" }}>
            <div><strong>Nom :</strong> {client.last_name}</div>
            <div><strong>Prenom :</strong> {client.first_name}</div>
            <div><strong>Email :</strong> {client.email}</div>
            <div><strong>Phone :</strong> {client.phone}</div>
            <button style={{ padding: "10px", marginTop: "10px" }} onClick={() => handleClientClick(client.customers_id)}>Voir ventes</button>
          </li>
        ))}
      </ul>
      <ul>

  {sales.map((sale, index) => (

    <li key={sale.sale_id} style={{ marginBottom: "10px" }}>

      <h3>Vente {index + 1} :</h3>
      <div><strong>ID de la vente :</strong> {sale.sale_id}</div>
      <div><strong>Total :</strong> {sale.total}</div>
    </li>
  ))}
</ul>

    </div>
  );
}

export default App;
