import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [noClientsFound, setNoClientsFound] = useState(false);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      if (response.data.length === 0) {
        setNoClientsFound(true);
        setClients([]);
      } else {
        setNoClientsFound(false);
        setClients(response.data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <input value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      {noClientsFound ? <p>No clients found with this name.</p> : null}
      <ul>
        {clients.map(client => (
          <li key={client.customers_id}>
            {client.last_name}, {client.first_name} - {client.email}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
