import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [noClients, setNoClients] = useState(false);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      if(response.data.length === 0) {
        setNoClients(true);
      } else {
        setNoClients(false);
        setClients(response.data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      backgroundColor: '#f5f5f5',
      padding: '10px'
    },
    input: {
      margin: '10px 0',
      padding: '10px',
      width: '200px',
      borderRadius: '5px',
      border: '1px solid #ddd'
    },
    button: {
      padding: '10px 20px',
      backgroundColor: '#007BFF',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer'
    },
    list: {
      listStyleType: 'none'
    },
    item: {
      padding: '10px',
      backgroundColor: '#fff',
      borderRadius: '5px',
      margin: '10px 0'
    }
  };

  return (
    <div style={styles.container}>
      <input style={styles.input} value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button style={styles.button} onClick={handleSearch}>Search</button>
      {noClients && <p>No clients found with this name</p>}
      <ul style={styles.list}>
        {clients.map(client => (
          <li key={client.customers_id} style={styles.item}>
          {client.last_name}, {client.first_name} - {client.email}
        </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
