import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [sales, setSales] = useState([]);
  const [selectedClientId, setSelectedClientId] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      setClients(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClientClick = async (clientId) => {
    setSelectedClientId(clientId);
    try {
      const response = await axios.get(`http://localhost:8000/sales/${clientId}/sales`);
      setSales(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <input value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {clients.map(client => (
          <li key={client.customers_id} onClick={() => handleClientClick(client.customers_id)}>
            {client.last_name}, {client.first_name} - {client.email}
          </li>
        ))}
      </ul>
      {selectedClientId && <h2>Sales for client {inputValue}</h2>}
      <ul>
        {sales.map(sale => (
          <li key={sale.sale_id}>
            {sale.created_at} - {sale.total} {sale.currency}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
