import React, { useState } from 'react';
import axios from 'axios';
import backgroundImage from './Bill.jpg'; 
import './App.css';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [client, setClient] = useState(null);
  const [sales, setSales] = useState([]);

  const handleSearch = async () => {
    const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
    setClient(response.data[0]); // Prend le premier client qui correspond à la recherche
  };

  const handleClientClick = async () => {
    const response = await axios.get(`http://localhost:8000/sales/${client.customers_id}`);
    setSales(response.data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <img src={backgroundImage} alt="background" style={{width: '100%', height: '40vh', objectFit: 'cover'}} />

      <h1>Apprenticeship - Test technique Full-Stack</h1>

      <h3>Veuillez entrer un nom :</h3>
      <input style={{ padding: "10px", marginBottom: "20px" }} placeholder='Entrer un Nom' value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button style={{ padding: "10px", marginLeft: "10px" }} onClick={handleSearch}>Rechercher</button>

      {client && (
        <div onClick={handleClientClick} style={{ cursor: "pointer", marginBottom: "20px" }}>
          <h1>Resultat de recherche :</h1>
          <p><strong>Nom :</strong> {client.last_name}</p>
          <p><strong>Prenom :</strong> {client.first_name}</p>
          <p><strong>Email :</strong> {client.email}</p>
          <p><strong>Phone :</strong> {client.phone}</p>
        </div>
      )}

      {sales.map((sale, index) => (
        <div key={sale.sale_id}>
          <h3>Vente {index + 1} :</h3>
          <p><strong>ID de la vente :</strong> {sale.sale_id}</p>
          <p><strong>Total :</strong> {sale.total}</p>
        </div>
      ))}
    </div>
  );
}

export default App;
