import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [sales, setSales] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      setClients(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClientClick = async (customerId) => {
    try {
      const response = await axios.get(`http://localhost:8000/sales/${customerId}?page=1`);
      setSales(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const loadMoreSales = async (customerId) => {
    try {
      setCurrentPage(currentPage + 1);
      const response = await axios.get(`http://localhost:8000/sales/${customerId}?page=1`);
      setSales([...sales, ...response.data]);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <input value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {clients.map(client => (
          <li key={client.customers_id} onClick={() => handleClientClick(client.customers_id)}>
            {client.last_name}, {client.first_name} - {client.email}
          </li>
        ))}
      </ul>
      {sales.length > 0 && (
        <div>
          <h2>Sales</h2>
          <ul>
            {sales.map(sale => (
              <li key={sale.id}>
                {sale}
              </li>
            ))}
          </ul>
          <button onClick={() => loadMoreSales()}>Load more sales</button>
        </div>
      )}
    </div>
  );
}

export default App;
