import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [sales, setSales] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      setClients(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClientClick = async (customerId) => {
    try {
      const response = await axios.get(`http://localhost:8000/sales/${customerId}`);
      setSales(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Vueillez entrez un Nom : </h2>
      <input value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      <ul>
        {clients.map(client => (
          <li key={client.customers_id} onClick={() => handleClientClick(client.customers_id)}>
            <h1>Client : </h1>
            Last_{client.last_name}, {client.first_name} - {client.email}
          </li>
        ))}
      </ul>
      <ul>
        {sales.map(sale => (
          <li key={sale.sale_id}>{sale.data } - {sale.total}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
