import React, { useState } from 'react';
import axios from 'axios';
import backgroundImage from './Bill.jpg'; 
import './App.css';

// Le composant Client rend les informations sur un client
function Client({client, onClick}) {
  return (
    <li style={{ marginBottom: "20px", cursor: "pointer" }} onClick={() => onClick(client)}>
      <div><strong>Nom :</strong> {client.last_name}</div>
      <div><strong>Prenom :</strong> {client.first_name}</div>
      <div><strong>Email :</strong> {client.email}</div>
      <div><strong>Phone :</strong> {client.phone}</div>
    </li>
  )
}

// Le composant Sales rend les informations sur les ventes d'un client
function Sales({sales}) {
  return (
    <>
      <h1>Ventes du client :</h1>
      <ul>
        {sales.map((sale, index) => (
          <li key={sale.sale_id} style={{ marginBottom: "10px" }}>
            <h3>Vente {index + 1} :</h3>
            <div><strong>ID de la vente :</strong> {sale.sale_id}</div>
            <div><strong>Total :</strong> {sale.total}</div>
          </li>
        ))}
      </ul>
    </>
  )
}

function App() {
  const [inputValue, setInputValue] = useState('');
  const [clients, setClients] = useState([]);
  const [sales, setSales] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/Client/${inputValue}`);
      setClients(response.data);
      setSelectedClient(null);
      setSales([]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClientClick = async (client) => {
    setSelectedClient(client);
    try {
      const response = await axios.get(`http://localhost:8000/sales/${client.customers_id}`);
      setSales(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <img src={backgroundImage} alt="background" style={{width: '100%', height: '40vh', objectFit: 'cover'}} />
      <h1>Apprenticeship - Test technique Full-Stack</h1>
      <h3>Veuillez entrer un nom :</h3>
      <input style={{ padding: "10px", marginBottom: "20px" }} placeholder='Entrer un Nom' value={inputValue} onChange={e => setInputValue(e.target.value)} />
      <button style={{ padding: "10px", marginLeft: "10px" }} onClick={handleSearch}>Rechercher</button>
      <h1>Résultats de recherche :</h1>
      {clients.length === 0 && <p>Aucun client trouvé avec ce nom.</p>}
      <ul>
        {clients.map(client => <Client key={client.customers_id} client={client} onClick={handleClientClick} />)}
      </ul>
      {selectedClient && <Sales sales={sales} />}
    </div>
  );
}

export default App;
